mapper.select:
select 

from dj_sysqxx
where QYID = #{qyid,jdbcType=INTEGER}
  and SEWBZ = #{sewbz,jdbcType=VARCHAR}

mapper.delete:
delete from dj_sysqxx
where QYID = #{qyid,jdbcType=INTEGER}
  and SEWBZ = #{sewbz,jdbcType=VARCHAR}

mapper.update:
update dj_sysqxx
set QYMC = #{qymc,jdbcType=VARCHAR},
  NSRSBH = #{nsrsbh,jdbcType=VARCHAR},
  DQDM = #{dqdm,jdbcType=VARCHAR},
  XM = #{xm,jdbcType=VARCHAR},
  LXDH = #{lxdh,jdbcType=VARCHAR},
  DJSJ = #{djsj,jdbcType=TIMESTAMP}
where QYID = #{qyid,jdbcType=INTEGER}
  and SEWBZ = #{sewbz,jdbcType=VARCHAR}
